(function(){"use strict";window.TeamSQL=function(){};window.TeamSQL.initHome=function(){this._initBrandsCarousel();this._initTestimonialsSlider();this._initTestimonialsSliderNav();};window.TeamSQL._initBrandsCarousel=function(){$('#brands-carousel').slick({infinite:true,slidesToShow:6,slidesToScroll:1,autoplay:true,autoplaySpeed:2500,responsive:[{breakpoint:1024,settings:{slidesToShow:3,slidesToScroll:3}},{breakpoint:600,settings:{slidesToShow:3,slidesToScroll:3}},{breakpoint:480,settings:{slidesToShow:2,slidesToScroll:2,arrows:false}}]});};window.TeamSQL._initNavbarActive=function(){$.each($('#top-navbar li'),function(index,navItem){if($(navItem).find('a').attr('href')===window.location.pathname){$(navItem).addClass('active');}});};window.TeamSQL._initTestimonialsSlider=function(){$('#testimonials-slider').slick({infinite:false,slidesToShow:2,slidesToScroll:1,arrows:false,dots:false,centerMode:false,initialSlide:0,centerPadding:'0',variableWidth:true});};window.TeamSQL._initTestimonialsSliderNav=function(){var testimonialsSlick=$('#testimonials-slider').slick('getSlick');var slideCount=testimonialsSlick.slideCount;for(var i=1;i<=slideCount;i++){var navDot;if(testimonialsSlick.currentSlide==(i-1)){navDot=$('<span class="slider-dot active" onclick="window.TeamSQL.handleTestimonialsSliderNavEvents(\'slickGoTo\', '+(i-1)+'); return false;"><i></i></span>');}else{navDot=$('<span class="slider-dot" onclick="window.TeamSQL.handleTestimonialsSliderNavEvents(\'slickGoTo\', '+(i-1)+'); return false;"><i></i></span>');}$('#testimonials-slider-nav .dots').append(navDot);}$('#testimonials-slider').on('afterChange',function(slick,currentSlide){$.each($('#testimonials-slider-nav .dots').children(),function(i,e){if(i==currentSlide.currentSlide){$(e).addClass('active');}else{$(e).removeClass('active');}})});};window.TeamSQL.handleTestimonialsSliderNavEvents=function(event,args){$('#testimonials-slider').slick(event,args);};})();$(document).ready(function(){window.TeamSQL._initNavbarActive();window.featuresMobileNavbar=$('.features-mobile-navbar ul').slick({variableWidth:true,infinite:false,dots:false,arrows:false,mobileFirst:true,swipeToSlide:true});});